import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";

const Dashboard = ({ updateProducts }) => {
  const [products, setProducts] = useState([]);
  const [productName, setProductName] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productImage, setProductImage] = useState("");
  const [editingProduct, setEditingProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    fetch("http://localhost:3000/products")
      .then((response) => response.json())
      .then((data) => setProducts(data))
      .catch((error) => console.error("Error al obtener productos:", error));
  };

  const handleAddProduct = () => {
    const newProduct = {
      name: productName,
      description: productDescription,
      price: parseFloat(productPrice),
      image: productImage,
    };

    fetch("http://localhost:3000/products", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newProduct),
    })
      .then((response) => response.json())
      .then((data) => {
        alert("Producto agregado:", data);
        fetchProducts();
        clearForm();
      })
      .catch((error) => console.error("Error al agregar producto:", error));
  };

  const handleUpdateProduct = () => {
    if (!editingProduct) return;

    const updatedProduct = {
      name: productName,
      description: productDescription,
      price: parseFloat(productPrice),
      image: productImage,
    };

    fetch(`http://localhost:3000/products/${editingProduct.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedProduct),
    })
      .then((response) => response.json())
      .then((data) => {
        alert("Producto actualizado:", data);
        fetchProducts();
        clearForm();
        setEditingProduct(null);
      })
      .catch((error) => console.error("Error al actualizar producto:", error));
  };

  const handleDeleteProduct = (productId) => {
    fetch(`http://localhost:3000/products/${productId}`, {
      method: "DELETE",
    })
      .then(() => {
        alert("Producto eliminado");
        fetchProducts();
      })
      .catch((error) => console.error("Error al eliminar producto:", error));
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setProductName(product.name);
    setProductDescription(product.description);
    setProductPrice(product.price.toString());
    setProductImage(product.image);
  };

  const clearForm = () => {
    setProductName("");
    setProductDescription("");
    setProductPrice("");
    setProductImage("");
  };

  const handleFinishChanges = () => {
    navigate("/");
  };

  return (
    <div className="crud-container">
      <h2>DASHBOARD</h2>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Descripción:</label>
        <input
          type="text"
          value={productDescription}
          onChange={(e) => setProductDescription(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          value={productPrice}
          onChange={(e) => setProductPrice(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Imagen:</label>
        <input
          type="text"
          value={productImage}
          onChange={(e) => setProductImage(e.target.value)}
        />
      </div>
      {editingProduct ? (
        <button className="btn btn-update" onClick={handleUpdateProduct}>
          Actualizar Producto
        </button>
      ) : (
        <button className="btn btn-add" onClick={handleAddProduct}>
          Añadir Producto
        </button>
      )}
      <button className="btn btn-finish" onClick={handleFinishChanges}>
        Finalizar
      </button>

      <h3>Lista de Productos</h3>
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Precio</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.description}</td>
              <td>${product.price}</td>
              <td>
                <button onClick={() => handleEditProduct(product)}>
                  Editar
                </button>
                <button onClick={() => handleDeleteProduct(product.id)}>
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
