import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";

const Dashboard = ({ products, updateProducts }) => {
  const [productName, setProductName] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productImage, setProductImage] = useState("");
  const navigate = useNavigate();

  const handleAddProduct = () => {
    const newProduct = {
      id: products.length + 1, // Generamos un nuevo ID
      name: productName,
      description: productDescription,
      price: parseFloat(productPrice),
      image: productImage,
    };

    updateProducts([...products, newProduct]); // Actualiza los productos

    setProductName("");
    setProductDescription("");
    setProductPrice("");
    setProductImage("");
  };

  const handleFinishChanges = () => {
    navigate("/");
  };

  return (
    <div className="crud-container">
      <h2>DASHBOARD</h2>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Descripcion:</label>
        <input
          type="text"
          value={productDescription}
          onChange={(e) => setProductDescription(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          value={productPrice}
          onChange={(e) => setProductPrice(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Imagen URL:</label>
        <input
          type="text"
          value={productImage}
          onChange={(e) => setProductImage(e.target.value)}
        />
      </div>
      <button className="btn btn-add" onClick={handleAddProduct}>
        Añadir Producto
      </button>{" "}
      {/* Aquí se usa la función para agregar un producto */}
      <button className="btn btn-finish" onClick={handleFinishChanges}>
        Finalizar
      </button>
      {"/"}
      {/* Botón para finalizar cambios y redirigir */}
    </div>
  );
};

export default Dashboard;
