import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";
import { image } from "cloudinary-react";

const Dashboard = ({ updateProducts }) => {
  const [products, setProducts] = useState([]);
  const [productName, setProductName] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productImage, setProductImage] = useState(null);
  const [editingProduct, setEditingProduct] = useState(null);
  const navigate = useNavigate();
  const uploadPreset = "camisetas";
  const cloudName = "dbwcqsa4m";

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    fetch("http://localhost:3000/products")
      .then((response) => response.json())
      .then((data) => setProducts(data))
      .catch((error) => console.error("Error al obtener productos:", error));
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append("file", file);
    formData.append("upload_preset", uploadPreset);

    try {
      const res = await axios.post(
        "https://api.cloudinary.com/v1_1/${cloudName}/image/upload, formData"
      );
      setImageUrl(res.data.secure_url);
    } catch (err) {
      console.error("Error al subir la imagen:", err);
    }
  };

  const handleImageChange = (e) => {
    setProductImage(e.target.files[0]);
  };

  const handleAddProduct = async () => {
    const formData = new FormData();
    formData.append("name", productName);
    formData.append("description", productDescription);
    formData.append("price", productPrice);
    formData.append("image", productImage);

    try {
      const response = await fetch("http://localhost:3000/upload", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      console.log("Producto agregado:", data);
      fetchProducts();
      clearForm();
    } catch (error) {
      console.error("Error al agregar producto:", error);
    }
  };

  const handleUpdateProduct = async () => {
    if (!editingProduct) return;

    const formData = new FormData();
    formData.append("name", productName);
    formData.append("description", productDescription);
    formData.append("price", productPrice);
    if (productImage) {
      formData.append("image", productImage);
    }

    try {
      const response = await fetch(
        `http://localhost:3000/products/${editingProduct.id}`,
        {
          method: "PUT",
          body: formData,
        }
      );
      const data = await response.json();
      console.log("Producto actualizado:", data);
      fetchProducts();
      clearForm();
      setEditingProduct(null);
    } catch (error) {
      console.error("Error al actualizar producto:", error);
    }
  };

  const handleDeleteProduct = (productId) => {
    fetch(`http://localhost:3000/products/${productId}`, {
      method: "DELETE",
    })
      .then(() => {
        console.log("Producto eliminado");
        fetchProducts();
      })
      .catch((error) => console.error("Error al eliminar producto:", error));
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setProductName(product.name);
    setProductDescription(product.description);
    setProductPrice(product.price.toString());
    setProductImage(null);
  };

  const clearForm = () => {
    setProductName("");
    setProductDescription("");
    setProductPrice("");
    setProductImage(null);
  };

  const handleFinishChanges = () => {
    navigate("/");
  };

  return (
    <div className="crud-container">
      <h2>DASHBOARD</h2>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Descripción:</label>
        <input
          type="text"
          value={productDescription}
          onChange={(e) => setProductDescription(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          value={productPrice}
          onChange={(e) => setProductPrice(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Imagen:</label>
        <input type="file" onChange={handleImageChange} />
      </div>
      {editingProduct ? (
        <button className="btn btn-update" onClick={handleUpdateProduct}>
          Actualizar Producto
        </button>
      ) : (
        <button className="btn btn-add" onClick={handleAddProduct}>
          Añadir Producto
        </button>
      )}
      <button className="btn btn-finish" onClick={handleFinishChanges}>
        Finalizar
      </button>

      <h3>Lista de Productos</h3>
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Precio</th>
            <th>Imagen</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.description}</td>
              <td>${product.price}</td>
              <td>
                <img
                  src={product.image}
                  alt={product.name}
                  style={{ width: "50px", height: "50px" }}
                />
              </td>
              <td>
                <button onClick={() => handleEditProduct(product)}>
                  Editar
                </button>
                <button onClick={() => handleDeleteProduct(product.id)}>
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
