// ProductList.jsx
import React from "react";
import "./productlist.css";

const ProductList = ({ products }) => {
  const mercadoPagoBaseUrl = "https://www.mercadopago.com/checkout/v1/redirect";
  const mercadoPagoPreference = {
    items: [
      {
        title: "", // Se actualizará con el nombre del producto
        description: "", // Se actualizará con la descripción del producto
        quantity: 1,
        currency_id: "ARS", // Código de moneda (puedes cambiarlo según tu ubicación)
        picture_url: "", // Se actualizará con la URL de la imagen del producto
        unit_price: 0, // Se actualizará con el precio del producto
      },
    ],
    back_urls: {
      success: "http://localhost:3000/success", // URL de éxito (puedes cambiarla)
      failure: "http://localhost:3000/failure", // URL de fallo (puedes cambiarla)
    },
  };

  const createMercadoPagoUrl = (product) => {
    const preference = { ...mercadoPagoPreference };
    preference.items[0].title = product.name;
    preference.items[0].description = product.description;
    preference.items[0].picture_url = product.image;
    preference.items[0].unit_price = product.price * 100; // El precio debe estar en centavos

    const queryParams = new URLSearchParams(preference);
    return `${mercadoPagoBaseUrl}?${queryParams.toString()}`;
  };

  return (
    <section className="container-productos">
      {products.length === 0 ? (
        <p>Cargando productos...</p>
      ) : (
        products.map((product) => (
          <div key={product.id} className="container-producto">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p className="precio-producto">${product.price}</p>
            <a
              href={createMercadoPagoUrl(product)}
              target="_blank"
              rel="noopener noreferrer"
            >
              <button>Pagar</button>
            </a>
          </div>
        ))
      )}
    </section>
  );
};

export default ProductList;
