import React, { useEffect, useState } from "react";
import "./cart.css";
import { useNavigate } from "react-router-dom";
import { initMercadoPago, Wallet } from "@mercadopago/sdk-react";

const Cart = ({
  isOpen,
  toggleCart,
  cartItems,
  updateQuantity,
  removeFromCart,
  total,
}) => {
  const navigate = useNavigate();
  const [preferenceId, setPreferenceId] = useState(null);

  useEffect(() => {
    initMercadoPago("TEST-d47fcab0-8cbe-4477-a6a2-10481ec983e8");
  }, []);

  if (!isOpen) return null;

  const handlePayment = async () => {
    const currentUser = JSON.parse(localStorage.getItem("currentUser"));
    if (currentUser) {
      try {
        const response = await fetch(
          "http://localhost:3000/create_preference",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              items: cartItems.map((item) => ({
                id: item.id,
                title: item.name,
                quantity: item.quantity,
                unit_price: item.price,
              })),
              total: total,
            }),
          }
        );
        const data = await response.json();
        setPreferenceId(data.id);
      } catch (error) {
        console.error("Error al crear la preferencia:", error);
        alert(
          "Hubo un error al procesar el pago. Por favor, intenta de nuevo."
        );
      }
    } else {
      alert("Necesitas iniciar sesión para realizar el pago.");
      navigate("/login");
    }
  };

  return (
    <div className={"cart-sidebar"}>
      <button className="close-cart" onClick={toggleCart}>
        ×
      </button>
      <h2>Carrito de Compras</h2>
      {cartItems.length === 0 ? (
        <p>El carrito está vacío</p>
      ) : (
        <>
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <img
                src={item.image}
                alt={item.name}
                className="cart-item-image"
              />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>Precio: ${item.price}</p>
                <p>Cantidad: {item.quantity}</p>
                <div className="cart-item-buttons">
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity - 1)}
                  >
                    -
                  </button>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity + 1)}
                  >
                    +
                  </button>
                  <button onClick={() => removeFromCart(item.id)}>
                    Eliminar
                  </button>
                </div>
              </div>
            </div>
          ))}
          <div className="cart-total">
            <h3>Total: ${total.toFixed(2)}</h3>
            <button onClick={handlePayment}>Iniciar pago</button>
            {preferenceId && <Wallet initialization={{ preferenceId }} />}
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
