import React from "react";
import "./cart.css";
import { useNavigate } from "react-router-dom";

const Cart = ({
  isOpen,
  toggleCart,
  cartItems,
  updateQuantity,
  removeFromCart,
  total,
}) => {
  const navigate = useNavigate();

  if (!isOpen) return null;

  const handlePayment = () => {
    const currentUser = JSON.parse(localStorage.getItem("currentUser"));
    if (currentUser) {
      // Aquí renderizamos el botón de Mercado Pago
      const script = document.createElement("script");
      script.src =
        "https://www.mercadopago.com.ar/integrations/v1/web-payment-checkout.js";
      script.setAttribute(
        "data-preference-id",
        "1273324264-f92cada3-65b7-4a53-a55b-af7cfb015eb6"
      );
      script.setAttribute("data-source", "button");
      document.getElementById("mercadopago-button").innerHTML = "";
      document.getElementById("mercadopago-button").appendChild(script);
    } else {
      alert("Necesitas iniciar sesión para realizar el pago.");
      navigate("/login");
    }
  };

  return (
    <div className={"cart-sidebar"}>
      <button className="close-cart" onClick={toggleCart}>
        ×
      </button>
      <h2>Carrito de Compras</h2>
      {cartItems.length === 0 ? (
        <p>El carrito está vacío</p>
      ) : (
        <>
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <img
                src={item.image}
                alt={item.name}
                className="cart-item-image"
              />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>Precio: ${item.price}</p>
                <p>Cantidad: {item.quantity}</p>
                <div className="cart-item-buttons">
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity - 1)}
                  >
                    -
                  </button>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity + 1)}
                  >
                    +
                  </button>
                  <button onClick={() => removeFromCart(item.id)}>
                    Eliminar
                  </button>
                </div>
              </div>
            </div>
          ))}
          <div className="cart-total">
            <h3>Total: ${total.toFixed(2)}</h3>
            <button onClick={handlePayment}>Iniciar pago</button>
            <div id="mercadopago-button"></div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
