import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = ({ products, updateProducts }) => {
  // Aquí recibes las funciones como props

  const [productName, setProductName] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [productPrice, setProductPrice] = useState("");

  const navigate = useNavigate();

  // Función para agregar un nuevo producto
  const handleAddProduct = () => {
    const newProduct = {
      id: products.length + 1, // Generamos un nuevo ID
      name: productName,
      description: productDescription,
      price: parseFloat(productPrice),
    };

    updateProducts([...products, newProduct]); // Usamos la función para actualizar los productos

    setProductName(""); // Limpiamos los campos del formulario después de agregar el producto
    setProductDescription("");
    setProductPrice("");
  };

  // Función para manejar el cambio de nombre del producto
  const handleProductNameChange = (e) => {
    setProductName(e.target.value);
  };

  // Función para manejar el cambio de descripción del producto
  const handleProductDescriptionChange = (e) => {
    setProductDescription(e.target.value);
  };

  // Función para manejar el cambio de precio del producto
  const handleProductPriceChange = (e) => {
    setProductPrice(e.target.value);
  };

  // Función para finalizar los cambios y redirigir a la página principal
  const handleFinishChanges = () => {
    navigate("/");
  };

  return (
    <div className="crud-container">
      <h2>DASHBOARD</h2>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          value={productName}
          onChange={handleProductNameChange}
        />
      </div>
      <div className="form-group">
        <label>Descripcion:</label>
        <input
          type="text"
          value={productDescription}
          onChange={handleProductDescriptionChange}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          value={productPrice}
          onChange={handleProductPriceChange}
        />
      </div>
      <button className="btn btn-add" onClick={handleAddProduct}>
        Add Product
      </button>{" "}
      {/* Aquí se usa la función para agregar un producto */}
      <button className="btn btn-finish" onClick={handleFinishChanges}>
        Finish Changes
      </button>{" "}
      {/* Botón para finalizar cambios y redirigir */}
    </div>
  );
};

export default Dashboard;
