import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./crud.css";

const Crud = ({ products, updateProducts }) => {
  // Aquí recibes las funciones como props
  const [productName, setProductName] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const navigate = useNavigate;

  const handleAddProduct = () => {
    // Esta función agrega un nuevo producto
    const newProduct = {
      id: products.length + 1, // Generamos un nuevo ID
      name: productName,
      description: productDescription,
      price: parseFloat(productPrice),
    };

    updateProducts([...products, newProduct]); // Usamos la función para actualizar los productos

    setProductName(""); // Limpiamos los campos del formulario después de agregar el producto
    setProductDescription("");
    setProductPrice("");
  };

  return (
    <div className="crud-container">
      <h2>DASHBOARD</h2>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Descripcion:</label>
        <input
          type="text"
          value={productDescription}
          onChange={(e) => setProductDescription(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          value={productPrice}
          onChange={(e) => setProductPrice(e.target.value)}
        />
      </div>
      <button className="btn btn-add" onClick={handleAddProduct}>
        Add Product
      </button>{" "}
      {/* Aquí se usa la función para agregar un producto */}
      <button className="btn btn-finish" onClick={() => history.push("/")}>
        Finish Changes
      </button>{" "}
      {/* Botón para finalizar cambios y redirigir */}
    </div>
  );
};

export default Crud;
