import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";

const Dashboard = ({ products, updateProducts }) => {
  // Estados para almacenar los valores del formulario y la imagen seleccionada
  const [productName, setProductName] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [productPrice, setProductPrice] = useState("");

  // Obtener la función de navegación de React Router
  const navigate = useNavigate();

  // Función para manejar el envío del formulario y agregar un nuevo producto
  const handleAddProduct = () => {
    // Crear un objeto FormData para enviar los datos del formulario, incluida la imagen
    const formData = new FormData();
    formData.append("name", productName);
    formData.append("description", productDescription);
    formData.append("price", parseFloat(productPrice));

    // Enviar una solicitud POST al servidor JSON para agregar un nuevo producto
    fetch("http://localhost:3000/products", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((newProduct) => {
        // Actualizar el estado de productos con el nuevo producto
        updateProducts([...products, newProduct]);
        // Limpiar los campos del formulario
        setProductName("");
        setProductDescription("");
        setProductPrice("");

        // Redirigir al usuario a la página principal después de agregar el producto
        navigate("/");
      })
      .catch((error) => console.error("Error al agregar producto:", error));
  };

  // Funciones para manejar los cambios en los campos del formulario
  const handleProductNameChange = (e) => {
    setProductName(e.target.value);
  };

  const handleProductDescriptionChange = (e) => {
    setProductDescription(e.target.value);
  };

  const handleProductPriceChange = (e) => {
    setProductPrice(e.target.value);
  };

  // Función para finalizar los cambios y redirigir a la página principal
  const handleFinishChanges = () => {
    navigate("/");
  };

  return (
    <div className="crud-container">
      <h2>DASHBOARD</h2>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          value={productName}
          onChange={handleProductNameChange}
        />
      </div>
      <div className="form-group">
        <label>Descripcion:</label>
        <input
          type="text"
          value={productDescription}
          onChange={handleProductDescriptionChange}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          value={productPrice}
          onChange={handleProductPriceChange}
        />
      </div>

      <button className="btn btn-add" onClick={handleAddProduct}>
        Agregar Producto
      </button>
      {/* Botón para finalizar los cambios y redirigir a la página principal */}
      <button className="btn btn-finish" onClick={handleFinishChanges}>
        Finalizar Cambios
      </button>
    </div>
  );
};

export default Dashboard;
