import React from "react";
import { Container, Table, Button } from "react-bootstrap";

const Cart = () => {
  const cartItems = [
    // Aquí debes obtener los artículos del carrito desde la base de datos JSON
  ];

  return (
    <Container>
      <h2>Carrito de Compras</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Producto</th>
            <th>Precio</th>
            <th>Cantidad</th>
            <th>Total</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item) => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>${item.price}</td>
              <td>
                <input
                  type="number"
                  min="1"
                  value={item.quantity}
                  onChange={(e) =>
                    handleQuantityChange(item.id, e.target.value)
                  }
                />
              </td>
              <td>${item.price * item.quantity}</td>
              <td>
                <Button
                  variant="danger"
                  onClick={() => handleRemoveFromCart(item.id)}
                >
                  Eliminar
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <div>
        <h4>Total: ${calculateTotal()}</h4>
        <Button variant="success">Realizar Pedido</Button>
      </div>
    </Container>
  );
};

export default Cart;
