// ProductList.jsx
import React, { useState, useEffect } from "react";
import "./productlist.css";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const mercadoPagoBaseUrl = "https://www.mercadopago.com/checkout/v1/redirect";
  const mercadoPagoPreference = {
    items: [
      {
        title: "",
        description: "",
        quantity: 1,
        currency_id: "USD", // Código de moneda
        picture_url: "", // Actualiza la imagen
        unit_price: 0, // Actualiza el precio del producto
      },
    ],
    back_urls: {
      success: "http://localhost:3000/success",
      failure: "http://localhost:3000/failure",
    },
  };

  useEffect(() => {
    fetch("http://localhost:3000/products")
      .then((response) => response.json())
      .then((data) => setProducts(data))
      .catch((error) => console.error("Error al obtener productos:", error));
  }, []);

  const createMercadoPagoUrl = (product) => {
    const preference = { ...mercadoPagoPreference };
    preference.items[0].title = product.name;
    preference.items[0].description = product.description;
    preference.items[0].picture_url = product.image;
    preference.items[0].unit_price = product.price * 100;

    const queryParams = new URLSearchParams(preference);
    return `${mercadoPagoBaseUrl}?${queryParams.toString()}`;
  };

  return (
    <section className="container-productos">
      {products.length === 0 ? (
        <p>Cargando productos...</p>
      ) : (
        products.map((product) => (
          <div key={product.id} className="container-producto">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p className="precio-producto">${product.price}</p>
            <a
              href={createMercadoPagoUrl(product)}
              target="_blank"
              rel="noopener noreferrer"
            >
              <button>Pagar</button>
            </a>
          </div>
        ))
      )}
    </section>
  );
};

export default ProductList;
